import {useEffect, useState, } from 'react'
import Cliente from '../components/Cliente'



const Inicio = () => {

    const [clientes , setClientes] = useState([])

    useEffect( () => {
      const obtenerClientesApi = async () => {
         try {
           const urlApi = "http://localhost:4000/clientes"
           const respuesta = await fetch(urlApi)
           const resultado = await respuesta.json()
           setClientes(resultado);
         } catch (error) {
           console.log(error);
         }
      }
          obtenerClientesApi()
    }, [] )

  return (
     <>
      <div>
          <h2 className='nuevo-cliente'>CLIENTES</h2>
          <p className='llenar-los-campos'>Administra tus clientes</p>
       </div>

       <div className='contenedor-formulario'>
          <table className='table-table'>

             <thead className='thead-table'>
                  <tr>
                    <th className='th-table'>Nombre</th>
                    <th className='th-table'>Contacto</th>
                    <th className='th-table'>Empresa</th>
                    <th className='th-table'>Acciones</th>
                  </tr>
             </thead>

             <tbody>
                  { clientes.map( cliente => (
                    <Cliente 
                      key={cliente.id}/>
                  ) ) }
             </tbody>

          </table>
       </div>

     </>
  )
}

export default Inicio
